	id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY, 
	username VARCHAR(50) NOT NULL, 
	password_hash VARCHAR(1000) NOT NULL, 
	image_name VARCHAR(255), 
	is_admin BOOLEAN, 
	UNIQUE (username)
);
